import React, { useState, useEffect } from "react";
import { Container, Row, Col, Card, List, ListItem } from "react-bootstrap";
import API from "../utils/API";
import re2 from "../images/re2.png";
//import rdr from "../images/rdr2.png";
//import mgs from "../images/mgs.png";
//import witcher from "../images/witcher3.png";
//import apex from "../images/apex.png";
//import { Link } from "react-router-dom";
function Games() {
  // Setting our component's initial state
  const [games, setBooks] = useState([]);
  const [formObject, setFormObject] = useState({});

  // Load all books and store them with setBooks
  useEffect(() => {
    loadGames();
  }, []);

  // Loads all books and sets them to books
  function loadGames() {
    API.getGames()
      .then((res) => setGames(res.data))
      .catch((err) => console.log(err));
  }
}
class LandingPage extends Component {
  render() {
    return (
      <Container fluid className="main">
        <Row>
          <Col>
            <h3 className="welcome">
              Heres a list of video games we recomend based on your choices
            </h3>
          </Col>
        </Row>
        <Row className="output">
          <Col md="auto">
            <Card
              className="questions"
              bg="dark"
              text="white"
              style={{ width: "18rem" }}
            >
              <Card.Body>
                <Card.Img variant="top" src={re2} />
                <Card.Title className="questions"></Card.Title>
                <Card.Text className="questions">
                  {" "}
                  A remake of the classic resident evil 2 with modernised
                  graphics and new mechanics that is taking the speed running
                  community by storm.
                </Card.Text>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
    );
  }
}
export default LandingPage;
